import React from 'react';
import { useHistory } from 'react-router-dom';
import "../style_setting_pc.css"  // Import useHistory to handle navigation

const Header = () => {
    const history = useHistory(); // Initialize useHistory hook

    // Function to navigate to a specific route
    const navigateTo = (route) => {
        history.push(route);
    };

    return (
        <div className="header_pc">
            <img src="img/logo_pc_header.svg" alt="Logo" style={{ width: '176px', cursor: 'pointer' }} onClick={() => navigateTo('/home')} />
            <img src="img/cvtalog_button.svg" alt="Catalog" style={{ width: '146px', cursor: 'pointer' }} onClick={() => navigateTo('/catalog')} />

            <div className="header_pc_vision">
                <input className="sgerch_pc" type="text" placeholder="Поиск товара" />
                <img src="img/serch_header.svg" alt="Search" style={{ cursor: 'pointer' }} />
            </div>

            <div className="blocs_header_pc">
                <div className="bloc_header_pc" onClick={() => navigateTo('/personal_area_setting')}>
                    <img src="svg/User.svg" alt="User" />
                    <a style={{ fontSize: '10px', color: '#757575' }}>Кабинет</a>
                </div>
                <div className="bloc_header_pc" onClick={() => navigateTo('/favorites')}>
                    <img src="svg/Heart.svg" alt="Favorites" />
                    <a style={{ fontSize: '10px', color: '#757575' }}>Избранное</a>
                </div>
                <div className="bloc_header_pc" onClick={() => navigateTo('/cart')}>
                    <img src="svg/Cart.svg" alt="Cart" />
                    <a style={{ fontSize: '10px', color: '#757575' }}>Корзина</a>
                </div>
            </div>
        </div>
    );
};

export default Header;
