import React from 'react';

function RegistrationForm() {
  return (
    <div  >
      <div className="w-1/2 flex justify-center items-center bg-background">
        <img src="../img/background_entrance.jpg" alt="Logo" className="w-full h-full" />
      </div>
      <div className="w-1/2 flex justify-center items-center">
        <div className="bg-white rounded-l-3xl p-8">
          <h2 className="text-3xl mb-6">Регистрация</h2>
          <div className="mb-6">
            <div className="mb-4">
              <label htmlFor="name" className="text-sm block mb-2">Имя</label>
              <input type="text" id="name" name="name" placeholder="ваше имя" className="tlk-input" required />
            </div>
            <div className="mb-4">
              <label htmlFor="email" className="text-sm block mb-2">Email</label>
              <input type="email" id="email" name="email" placeholder="janedoe@mail.com" className="tlk-input" required />
            </div>
            <div className="mb-4">
              <label htmlFor="password" className="text-sm block mb-2">Пароль</label>
              <div className="password relative">
                <input type="password" id="password" name="password" placeholder="создайте свой пароль" className="tlk-input" />
                <a href="#" className="password-control absolute top-1/2 right-4 transform -translate-y-1/2" onClick={showHidePassword}></a>
              </div>
            </div>
          </div>
          <div className="mb-8">
            <button className="w-full py-3 bg-black text-white rounded-md hover:bg-gray-800 transition duration-300">Войти</button>
          </div>
          <div>
            <a href="login.html" className="tlk-form__link">Уже зарегистрированы? Войти тут</a>
          </div>
        </div>
      </div>
    </div>
  );
}

function showHidePassword(e) {
  e.preventDefault();
  const passwordInput = document.getElementById('password');
  const passwordControl = e.target;

  if (passwordInput.type === 'password') {
    passwordInput.type = 'text';
    passwordControl.classList.add('view');
  } else {
    passwordInput.type = 'password';
    passwordControl.classList.remove('view');
  }
}

export default RegistrationForm;
